Answer <pre><code>class Solution {
 public:
  vector&lt;int&gt; decrypt(vector&lt;int&gt;&amp; code, int k) {
    int n = code.size();
    vector&lt;int&gt; ans(n, 0);
    if (k == 0) {
      return ans;
    }
    code.resize(n * 2);
    copy(code.begin(), code.begin() + n, code.begin() + n);
    int l = k &gt; 0 ? 1 : n + k;
    int r = k &gt; 0 ? k : n - 1;
    int sum = 0;
    for (int i = l; i &lt;= r; i++) {
      sum += code[i];
    }
    for (int i = 0; i &lt; n; i++) {
      ans[i] = sum;
      sum -= code[l];
      sum += code[r + 1];
      l++;
      r++;
    }
    return ans;
  }
};
</code></pre>
